import React, {Component} from 'react'
import ReactDOM from 'react-dom';
import Table from './Table'
import {$,jQuery} from 'jquery'
import _ from 'lodash'
import 'bootstrap/dist/css/bootstrap.min.css'
//import { DropDownList } from '@progress/kendo-react-dropdowns';
//import Dropdown from './Dropdown.js';

class Article extends Component {

    async componentDidMount() {
        const response = await fetch('https://jsonplaceholder.typicode.com/posts')
        const data = await response.json()

        console.log(data)

        this.setState({
            data:data
        })
    }

    state = {
        isOpen: false,
        data: [],
        sort: 'asc',
        sortField: 'title'
    }

    onSort = sortField => {
        const clonedData = this.state.data.concat()
        const sortType =  /*this.state.sort === 'asc' ? 'desc' :*/ 'asc'
        const orderedData = _.orderBy(clonedData, sortField, sortType)

        console.log(this.state.sortField)

        this.setState({
            data: orderedData,
            sort: sortType,
            sortField
        })
    }

    constructor(props) {
        super(props);
        this.state = {value: 'id'};

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }

      handleChange(event) {
        this.setState({value: event.target.value});
      }

      handleSubmit(event) {
        alert('Your favorite flavor is: ' + this.state.value);
        this.onSort.bind(null, this.state.value)
        event.preventDefault();
      }

    render() {
        const body = this.state.isOpen && <Table data={this.state.data} /*onSort={this.onSort} *//>
        return (
            <div>

                <h2>Change Data Base to : 
                    <button onClick={this.handleClick}>
                        {this.state.isOpen ? 'close' : 'open'}
                    </button>
                </h2>


                <h4>
                    Sorted by : 
                    <select  id = "dropdown" ref = {(input)=> this.menu = input}>
                        <option selected='id'  sortField='id' onChange ={console.log('id')}>ID</option>
                        <option onChange ={console.log('userId')} >User ID</option>
                        <option value="title" onChange ={console.log('title')} >Title</option>
                        <option  selected='body' sortField='body' onChange ={console.log('Body')}>Body</option>
                    </select>
                    <button onClick={this.onSort.bind(null,this.state.sortField)}>appy</button>
                </h4>


<form onSubmit={this.handleSubmit}>
          <label>
            Pick your favorite flavor:
            <select value={this.state.value} onChange={this.handleChange}>
        <option value="id">id</option>
        <option value="userId">userId</option>
              <option value="title">title</option>
              <option value="body">body</option>
            </select>
          </label>
          <input type="submit" value="Submit" />
        </form>
                {body}
            </div>
        )
    } 
    handleClick = () => {
        this.setState({
            isOpen: !this.state.isOpen
        })
    }
    }

