import React, {Component} from 'react'
import ReactDOM from 'react-dom';
import Table from './Table'
import {$,jQuery} from 'jquery'
import _ from 'lodash'
import 'bootstrap/dist/css/bootstrap.min.css'

class Article extends Component {

    async componentDidMount() {
        const response = await fetch('https://jsonplaceholder.typicode.com/posts')
        const data = await response.json()

        console.log(data)

        this.setState({
            data:data
        })
    }

    state = {
        isOpen: false,
        data: [],
        sort: 'asc',
        sortField: 'id'
    }

    onChangeSelect({target: {value}}) {
        console.log(value)
        this.onSort(value)
    }

    onSort = sortField => {
        const clonedData = this.state.data.concat()
        const sortType =  /*this.state.sort === 'asc' ? 'desc' :*/ 'asc'
        const orderedData = _.orderBy(clonedData, sortField, sortType)

        console.log(this.state.sortField)

        this.setState({
            data: orderedData,
            sort: sortType,
            sortField
        })
    }

    render() {
        const body = this.state.isOpen && <Table data={this.state.data} />
        return (
            <div>
                <h2>Change Data Base to : 
                    <button onClick={this.handleClick}>
                        {this.state.isOpen ? 'close' : 'open'}
                    </button>
                </h2>

                <h4>
                    Sorted by : 
                    <select onChange ={this.onChangeSelect} id = "dropdown" ref = {(input)=> this.menu = input}>
                        <option selected='id'  sortField='id'>ID</option>
                        <option>User ID</option>
                        <option value="title">Title</option>
                        <option  selected='body'>Body</option>
                    </select>
                </h4>

                {body}
            </div>
        )
    } 
    handleClick = () => {
        this.setState({
            isOpen: !this.state.isOpen
        })
    }
}

export default Article
