import React, {Component} from 'react'
import ReactDOM from 'react-dom';
import Table from './Table'
import {$,jQuery} from 'jquery'
import _ from 'lodash'
import 'bootstrap/dist/css/bootstrap.min.css'
import { DropDownList } from '@progress/kendo-react-dropdowns';
//import Dropdown from './Dropdown.js';

class Article extends Component {

    async componentDidMount() {
        const response = await fetch('https://jsonplaceholder.typicode.com/posts')
        const data = await response.json()

        console.log(data)

        this.setState({
            data:data
        })
    }

    state = {
        isOpen: false,
        data: [],
        sort: 'asc',
        sortField: 'id'
    }

    onSort = sortField => {
        const clonedData = this.state.data.concat()
        const sortType = this.state.sort === 'asc' ? 'desc' : 'asc'
        const orderedData = _.orderBy(clonedData, sortField, sortType)

        this.setState({
            data: orderedData,
            sort: sortType,
            sortField
        })

        const options = [
            'one', 'two', 'three'
          ]
          const defaultOption = options[0]
    }

    render() {
        const body = this.state.isOpen && <Table data={this.state.data} onSort={this.onSort} />
        return (
            <div>

                <h2>Change Data Base to : 
                    <button onClick={this.handleClick}>
                        {this.state.isOpen ? 'close' : 'open'}
                    </button>
                </h2>
                {body}
            </div>
        )
    } 
    handleClick = () => {
        this.setState({
            isOpen: !this.state.isOpen
        })
    }
    }

class AppComponent extends React.Component {
    sizes = [ "X-Small", "Small", "Medium", "Large", "X-Large", "2X-Large" ];

    render() {
        return (
            <div>
                <div>T-shirt size:</div>
                <DropDownList data={this.sizes} />
            </div>
        );
    }
}

    ReactDOM.render(
        <AppComponent />,
        document.querySelector('my-app')
    ); 
export default Article
