import React, {Component} from 'react'
import Table from './Table'
import _ from 'lodash'
import 'bootstrap/dist/css/bootstrap.min.css'

import Dropdown from './Dropdown.js';

class Article extends Component {

    async componentDidMount() {
        const response = await fetch('https://jsonplaceholder.typicode.com/posts')
        const data = await response.json()

        console.log(data)

        this.setState({
            data:data
        })
    }

    state = {
        isOpen: false,
        data: [],
        sort: 'asc',
        sortField: 'id'
    }

    onSort = sortField => {
        const clonedData = this.state.data.concat()
        const sortType = this.state.sort === 'asc' ? 'desc' : 'asc'
        const orderedData = _.orderBy(clonedData, sortField, sortType)

        this.setState({
            data: orderedData,
            sort: sortType,
            sortField
        })

        const options = [
            'one', 'two', 'three'
          ]
          const defaultOption = options[0]
    }

    render() {
        const body = this.state.isOpen && <Table data={this.state.data} onSort={this.onSort} />
        return (
            <div>
                <Dropdown />
                <h2>Change Data Base to : 
                    <button onClick={this.handleClick}>
                        {this.state.isOpen ? 'close' : 'open'}
                    </button>
                </h2>
                <select onChange={this.filterChamp.bind(this)} value={this.state.filterChamp}>
          <option value="">All Champs</option>
          <option value="Assassin">Assassin</option>
          <option value="Fighter">Fighter</option>
          <option value="Mage">Mage</option>
          <option value="Marksman">Marksman</option>
          <option value="Support">Support</option>
          <option value="Tank">Tank</option>
        </select>
                {body}
            </div>
        )
    } 
    handleClick = () => {
        this.setState({
            isOpen: !this.state.isOpen
        })
    }
}

export default Article
